import loggerMiddleware from './src/middlewares/logger.middleware';
import Controller from './src/types/controller';

const rateLimit = require('express-rate-limit');

const PRODUCTION_FOLDER = join(process.cwd(), 'dist');

class App {
	public app: express.Application;
	public port: number;
	private readonly production: boolean;

	constructor(controllers: Controller[], port: number, production = false) {
		this.app = express();
		this.port = port;
		this.production = production;

		this.initializeMiddlewares();
		this.initializeControllers(controllers);
		this.initializeEngine();
		this.initializeErrorHandling();
		listRoutes(this.app);
	}

	public listen() {
		this.app.listen(this.port, () => {
			console.log(`App listening on the port ${process.env.PORT}`);
		});
	}

	private initializeMiddlewares() {

		this.app.use(helmet());
		this.app.use(cors(corsOptions));
		this.app.use(compression());
		this.app.use(json({ limit: '20mb' }));
		this.app.use(urlencoded({ extended: true }));
		this.app.set('trust proxy', true);
		this.app.use(cookieParser());

		// this.setupRateLimiter();

		this.app.use(loggerMiddleware);
		this.app.use(passport.initialize());

	}

	private initializeErrorHandling() {
		this.app.use(errorMiddleware);
	}

	private initializeControllers(controllers: Controller[]) {
		this.app.get('/api/v1', (req, response) => {
			response.send('Dashboard v1');
		});

		controllers.forEach((controller) => {
			this.app.use('/api/v1', controller.router);
		});
	}

	private initializeEngine() {
		this.app.set('view engine', 'html');
		this.app.set('views', join(PRODUCTION_FOLDER, 'browser'));

		if (!this.production) {
			this.app.engine('html', require('ejs').renderFile);
		}

		// Server static files from /browser
		this.app.get(
			'*.*',
			express.static(join(PRODUCTION_FOLDER, 'browser'), {
				maxAge: '1y',
			})
		);

		// All regular routes use the Universal engine
		this.app.get(/^\/(?!api).*/, (req, res) => {
			if (this.production) {
				res.render('index', { req });
			} else {
				res.send({ response: 'API not found' });
			}
		});
	}

}

export default App;

function listRoutes(app) {
	app._router.stack.forEach(printRoutes.bind(this, []));
}

function printRoutes(path, layer) {
	if (layer.route) {
		layer.route.stack.forEach(
			printRoutes.bind(this, path.concat(splitRoutes(layer.route.path)))
		);
	} else if (layer.name === 'router' && layer.handle.stack) {
		layer.handle.stack.forEach(
			printRoutes.bind(this, path.concat(splitRoutes(layer.regexp)))
		);
	} else if (layer.method) {
		console.log(
			'	%s		|	/%s',
			layer.method.toUpperCase(),
			path
				.concat(splitRoutes(layer.regexp))
				.filter(Boolean)
				.join('/')
		);
	}
}

function splitRoutes(thing) {
	if (typeof thing === 'string') {
		return thing.split('/');
	} else if (thing.fast_slash) {
		return '';
	} else {
		let match = thing
			.toString()
			.replace('\\/?', '')
			.replace('(?=\\/|$)', '$')
			.match(/^\/\^((?:\\[.*+?^${}()|[\]\\\/]|[^.*+?^${}()|[\]\\\/])*)\$\//);
		return match
			? match[1].replace(/\\(.)/g, '$1').split('/')
			: '<complex:' + thing.toString() + '>';
	}
}

export { listRoutes };

import 'dotenv/config';
import { Authenticator } from 'passport';
import { ExtractJwt, Strategy as JWtStrategy } from 'passport-jwt';

const passport = new Authenticator();

const jwtStrategy = (
	payload: any,
	done: (error: any, user?: any, info?: any) => void
) => {
	const id = payload.sub;
	return id ? done(null, id) : done(new Error('You shall not pass'), null);
};

passport.use(
	new JWtStrategy(
		{
			secretOrKey: process.env.JWT_SECRET,
			jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken(),
		},
		jwtStrategy
	)
);

export default passport;

import * as cors from 'cors';

export const corsOptions: cors.CorsOptions = {
	origin: 'http://localhost:3000',
	methods: 'GET,HEAD,PUT,PATCH,POST,DELETE,OPTIONS',
	credentials: true,
	exposedHeaders: ['Authorization', 'Set-Cookie']
};

import { HttpException } from '../exceptions';
import { NextFunction, Request, Response } from 'express';

function errorMiddleware(error: HttpException, request: Request, response: Response, next: NextFunction) {
	const status = error.status >= 100 && error.status < 600 ? error.status : 500;
	const message = error.message || 'Something went wrong';

	console.error(error);
	if (response.headersSent) {
		return next(error);
	}
	response.status(status).send({
		status,
		message
	});
}
export default errorMiddleware;

