import Payload from '../types/payload';
import RequestWithUser from '../interfaces/requestWithUser';
import typy from 'typy';
import { AuthenticationTokenMissingException, WrongAuthenticationTokenException } from '../exceptions/index';
import { NextFunction, Response } from 'express';
import { verify } from 'jsonwebtoken';
import * as users from './../mocks/users.json';

async function authMiddleware(request: RequestWithUser, response: Response, next: NextFunction) {
	const authorization = typy(request, 'headers.authorization').safeObject as string;
	if (!authorization) {
		return next(new WrongAuthenticationTokenException());
	}
	const token = authorization.split(' ')[1];
	if (token) {
		const secret = process.env.JWT_SECRET;
		try {
			const verificationResponse = verify(token, secret) as Payload;
			const id = verificationResponse._id;
			const user = users.find(u => u._id === id);
			if (user) {
				request.user = user;
				next();
			} else {
				next(new WrongAuthenticationTokenException());
			}
		} catch (error) {
			next(new WrongAuthenticationTokenException());
		}
	} else {
		next(new AuthenticationTokenMissingException());
	}
}

export default authMiddleware;

import { NextFunction, Request, Response } from 'express';
import { HttpException } from '../exceptions';

function notFoundMiddleware(
	error: HttpException,
	request: Request,
	response: Response,
	next: NextFunction
) {
	const status = 404;
	const message = 'Element not found';
	if (response.headersSent) {
		return next(error);
	}
	response.status(status).send({
		status,
		message
	});
}
export default notFoundMiddleware;

import { WrongAuthenticationTokenException } from '../exceptions/index';
import RequestWithUser from '../interfaces/requestWithUser';
import { NextFunction, Response } from 'express';

async function selfMiddleware(
	request: RequestWithUser,
	response: Response,
	next: NextFunction
) {
	if (request.session.passport.user === request.user._id) {
		next();
	} else {
		next(new WrongAuthenticationTokenException());
	}
}

export default selfMiddleware;

import * as express from 'express';
import HttpException from '../exceptions/HttpException';
import { plainToClass } from 'class-transformer';
import { validate, ValidationError } from 'class-validator';

function validationMiddleware<T>(
	type: any,
	skipMissingProperties = false
): express.RequestHandler {
	return (request, response, next) => {
		validate(plainToClass(type, request.body), { skipMissingProperties }).then(
			(errors: ValidationError[]) => {
				if (errors.length > 0) {
					const message = errors
						.map((error: ValidationError) => Object.values(error.constraints))
						.join(', ');
					next(new HttpException(message, 400));
				} else {
					next();
				}
			}
		);
	};
}

export default validationMiddleware;

import Payload from '../types/payload';
import RequestWithUser from '../interfaces/requestWithUser';
import { NextFunction, Response } from 'express';
import { verify } from 'jsonwebtoken';
import t from 'typy';
import * as users from './../mocks/users.json';

async function userMiddleware(
	request: RequestWithUser,
	response: Response,
	next: NextFunction
) {
	try {
		const token = (t(request, 'headers.authorization')
			.safeObject as string).split(' ')[1];
		if (token) {
			const secret = process.env.JWT_SECRET;
			const verificationResponse = verify(token, secret) as Payload;
			const id = verificationResponse._id;
			const user = users.find(u => u._id === id);
			if (user) {
				request.user = user;
			} else {
				request.user = null;
			}
		} else {
			request.user = null;
		}
		next();
	} catch (error) {
		request.user = null;
		next();
	}
}

export default userMiddleware;

import { compare, genSalt, hash } from 'bcrypt-nodejs';

function generateHash(password: string, salt = 10): Promise<string> {
	return new Promise((resolve, reject) => {
		genSalt(salt, (err: Error, saltGenerated: string) => {
			if (err) {
				return reject(err);
			}
			hash(password, saltGenerated, null, (errHash: Error, hashGen: string) => {
				errHash ? reject(errHash) : resolve(hashGen);
			});
		});
	});
}

function comparePasswords(
	password: string,
	hashedPassword: string
): Promise<boolean> {
	return new Promise<boolean>((resolve, reject) => {
		compare(password, hashedPassword, (error, result) => {
			error ? reject(false) : !result ? resolve(false) : resolve(true);
		});
	});
}

export { generateHash, comparePasswords };

import { sign } from 'jsonwebtoken';
import moment from 'moment';
import { Payload, User } from './../../types/index';

class AuthenticationService {
	public createToken(user: User): string {
		const secret = process.env.JWT_SECRET;
		const expiresIn = moment().add(1, 'hour').unix();
		const payload: Payload = {
			_id: user._id,
			user: {
				_id: user._id,
				name: user.name,
				email: user.email,
				addresses: user.addresses,
				createdAt: user.createdAt,
			},
			expiresIn,
		};
		return sign(payload, secret, { expiresIn });
	}
}

export default AuthenticationService;

import { Request, Response, Router } from 'express';
import IController from 'interfaces/controller.interface';
import users from '../../mocks/users.json';
import { authMiddleware } from './../../middlewares/index';

class UserController implements IController {
	public path = '/users';
	public router = Router();

	constructor() {
		this.intializeRoutes();
	}

	public intializeRoutes() {
		this.router.get(this.path, this.getAllUsers);
		this.router
			.all(`${this.path}/*`, authMiddleware);
	}


	private getAllUsers = (request: Request, response: Response) => {
		response.send(users);
	}
}

export default UserController;

import { cleanEnv, str, port, bool } from 'envalid';

export default function validateEnv() {
	cleanEnv(process.env, {
		PRODUCTION: bool(),
		PORT: port(),
		JWT_SECRET: str(),
	});
}
